	DBO.MDF_ACT_GETBOOLEANVALUE_ACT_BYPARACTIDMETAVALUETYPE('RVCTFU1_STREPTOMYCINSUSCEPTIBLE',ACT_RVCTFU1.ID,'VALUEBOOL') AS RVCTFU1_STREPTOMYCINSUSCEPTIBLE,
