	DBO.MDF_ACT_GETSTRINGVALUE_ACT_BYPARACTIDMETAVALUETYPE('RVCTFU1_SUSCTESTINGDONEYES',ACT_RVCTFU1.ID,'EFFECTIVETIME_TXT') AS RVCTFU1_FIRSTISOLATECOLLECTED,
	DBO.MDF_ACT_GETDATEVALUE_ACT_BYPARACTIDMETAVALUETYPE('RVCTPG_DATECOUNTED',ACT_RVCTPG.ID,'EFFECTIVETIME_BEG') AS RVCTFU1_YEARCOUNTED,
	DBO.MDF_ACT_GETSTRINGVALUE_ACT_BYPARACTIDMETAVALUETYPE('RVCTPG_STATEREPORTING_FK',ACT_RVCTPG.ID,'TITLE') AS RVCTFU1_STATECASENUMBER,
	DBO.MDF_ACT_GET_MONTH_YEAR_ACT_DATETYPE('RVCTPG_STATEREPORTING_FK',ACT_RVCTPG.ID,0) AS RVCTFU1_STATEREPORTINGYEAR,
	DBO.MDF_ACT_GETSTRINGVALUE_ACT_BYPARACTIDMETAVALUETYPE('RVCTPG_CITYCOUNTYCASESTATE_FK',ACT_RVCTPG.ID,'TITLE') AS RVCTFU1_CITYCOUNTYCASENUMBER,
	DBO.MDF_ACT_GET_MONTH_YEAR_ACT_DATETYPE('RVCTPG_CITYCOUNTYCASESTATE_FK',ACT_RVCTPG.ID,0) AS RVCTFU1_CITYCOUNTYYEAR,
