	DBO.MDF_UCS_FULLNAME_ACTCHILDUCS_BYPARENTACTID('RVCTPG_CITYCOUNTYCASESTATE_FK', ACT_RVCTPG.ID, 'VALUECODE_ID') AS RVCTFU1_CITYCOUNTY
FROM 
	A_ACT ACT_CASE  (NOLOCK) 
	INNER JOIN A_ACT ACT_DOCBODY  (NOLOCK) ON ACT_CASE.ID=ACT_DOCBODY.ACT_CASENCM_ID AND ACT_DOCBODY.CLASSCODE='DOCBODY' AND ACT_DOCBODY.MOODCODE='EVN'
	INNER JOIN A_ACT ACT_RVCTFU1  (NOLOCK) ON ACT_RVCTFU1.ACT_PARENT_ID=ACT_DOCBODY.ID AND ACT_RVCTFU1.CLASSCODE = 'DOCSECT' AND ACT_RVCTFU1.MOODCODE = 'EVN' AND ACT_RVCTFU1.METACODE = 'RVCTFU1_ID'
	INNER JOIN A_ACT ACT_RVCTPG  (NOLOCK) ON ACT_RVCTPG.ACT_PARENT_ID=ACT_DOCBODY.ID AND ACT_RVCTPG.CLASSCODE = 'DOCSECT' AND ACT_RVCTPG.MOODCODE = 'EVN'  AND ACT_RVCTPG.METACODE = 'RVCTPG_ID'
	INNER JOIN E_ENTITY  (NOLOCK) ON E_ENTITY.ID=[DBO].MDF_ROLE_GETVALUE_ACTPARTICIPATIONROLE_BYACTID(ACT_CASE.ACT_PARENT_ID,'PLAYER_ID') 
	INNER JOIN T_ENTITYNAME ETN  (NOLOCK) ON ETN.ENTITY_ID=E_ENTITY.ID
	INNER JOIN T_ENTITYADDRESS ETA  (NOLOCK) ON ETA.ENTITY_ID=E_ENTITY.ID AND ETA.USECODE='H' 
WHERE 
	ACT_CASE.CLASSCODE='CASE' AND ACT_CASE.MOODCODE='EVN' AND ACT_CASE.STATUSCODE='ACTIVE'

